 CREATE PROCEDURE [dbo].[USP_CheckIAHasCompleted] ( 	@PatientID INT ) AS BEGIN 	DECLARE @ActivityID INT = 0; 	SELECT @ActivityID = Activity_ID  FROM Mst_Activity WHERE ActivityType='InitialAssessment'; 	PRINT @ActivityID  	IF @ActivityID != 0  	BEGIN  		--returning result if the IA is completed for the patient. 		SELECT Patient_Activity_ID,Activity_ID FROM Trn_Patient_Activity WHERE Activity_ID = @ActivityID AND Patient_ID = @PatientID AND Activity_Start_Status = 3 	END 	ELSE  	BEGIN 		--empty result returning 		SELECT Patient_Activity_ID,Activity_ID FROM Trn_Patient_Activity WHERE 1=2 	END END  --EXEC USP_CheckIAHasCompleted 25813