 Create Procedure [dbo].[USP_GetUserListByAccountRole] ( 	@AccountId INT ) AS BEGIN 	SELECT User_ID, Cust_ID,User_First_Name,User_Last_Name,User_Role,User_email,User_Phone,User_Status,User_Timezone,User_CreatedAt,User_CreatedBy,User_UpdatedAt,User_UpdatedBy  	FROM Mst_User U 	INNER JOIN MstAccountTypeRoles R ON U.User_Role = R.RoleId 	--INNER JOIN Trn_User_Account UA ON UA.UserId = U.User_Role 	INNER JOIN Mst_Account MA ON MA.AccountType = R.AccountTypeId AND MA.Account_ID = @AccountId END  --EXEC USP_GetUserListByAccountRole 22