 Create procedure USP_InsertOrUpdateUploadSummary ( 	@ID INT, 	@AccountId INT, 	@UploadType INT, 	@UploadDate DATETIME, 	@UploadedBy INT, 	@UploadStatus VARCHAR(50), 	@MemberCount INT, 	@ClaimCount INT, 	@PDCActivityCount INT, 	@ErrorText TEXT, 	@UploadPath NVARCHAR(MAX), 	@UpdatedRecordCount INT, 	@NewEnrollmentQueuecount INT )AS BEGIN 	DECLARE @UploadID INT; 	IF @ID = 0  	BEGIN  		INSERT INTO UploadSummary (UploadType,UploadDate,UploadedBy,UploadStatus,MemberCount,ClaimCount,PDCActivityCount,ErrorText,UploadPath, AccountID,UpdatedRecordCount,NewEnrollmentQueuecount) 		VALUES (@UploadType, @UploadDate,@UploadedBy,@UploadStatus,@MemberCount,@ClaimCount,@PDCActivityCount,@ErrorText,@UploadPath, @AccountId,@UpdatedRecordCount,@NewEnrollmentQueuecount); 		SET @UploadID = SCOPE_IDENTITY(); 	END  	ELSE  	BEGIN  		SET @UploadID = @ID; 		UPDATE UploadSummary SET UploadDate = @UploadDate,UploadedBy = @UploadedBy, 		UploadStatus = @UploadStatus, 		MemberCount = @MemberCount, 		ClaimCount = @ClaimCount, 		PDCActivityCount = @PDCActivityCount, 		ErrorText = @ErrorText, 		UploadPath = @UploadPath, 		UpdatedRecordCount = @UpdatedRecordCount, 		NewEnrollmentQueuecount = @NewEnrollmentQueuecount 		WHERE ID = @ID 	END 	SELECT @UploadID END