 CREATE procedure USP_NonBillingServicesDetails ( 	@AccountID INT, 	@fromDate DATETIME, 	@toDate DATETIME ) AS BEGIN 	SELECT  		DISTINCT TPA.Patient_ID, 		'' AS billing_code, 		TPA.DateOfService AS dos, 		--TBP.PatientID, 		TBP.PracticeName AS practice_name, 		TBP.ReferringProviderName AS primary_physician, 		U.User_First_Name +' ' + U.User_Last_Name AS bcm, 		P.Patient_First_Name +' ' + P.Patient_Last_Name AS patient_name, 		TBP.PrimaryDiagnosis AS primary_dx, 		D.ICD10Code AS reason_for_referral, 		AE.Discription AS patient_type 	FROM Trn_Patient_Activity TPA 	INNER JOIN Mst_Patient P ON TPA.Patient_ID = p.Patient_ID  AND P.Patient_Status =1 AND P.Account_ID = @AccountID 	LEFT JOIN TrnBillingProfile TBP ON TBP.PatientID = TPA.Patient_ID AND TBP.MonthOfService = TPA.DateOfService AND TBP.IsDeleted = 0 	LEFT JOIN TrnPatientAssessmentLog PAL ON PAL.PatientActivityID = TBP.PatientActivityID 	LEFT JOIN Mst_User U ON TPA.Activity_Assigned_To = U.User_ID 	LEFT JOIN Mst_Diagnosis D ON P.ReasonForReferral = D.Diagnosis_ID 	LEFT JOIN MstAssessmentEnum AE ON AE.EnumValue = PAL.ProgramType AND AE.EnumType='ProgramTypes' 	WHERE IsSubmited =1 AND TBP.PatientID IS NULL --AND IsBillable =0 	AND TPA.DateOfService >= @fromDate and TPA.DateOfService <= @toDate  END  --exec USP_NonBillingServicesDetails 22,'07/01/2022','07/31/2022'