 CREATE procedure USP_ServiceBillingSummaryReport ( 	@AccountID INT, 	@fromDate DATETIME, 	@toDate DATETIME ) AS BEGIN 	SELECT   		P.Patient_ID, 		P.Patient_First_Name +' ' + P.Patient_Last_Name AS PatientFullName, 		P.Patient_DOB, 		TBP.MonthOfService, --AS BillableDate, 		TBP.MonthOfService AS BillableDate, 		TBP.PrimaryPayerName, 		TBP.PrimaryPayerMemberID, 		TBP.PrimaryPayerGroupNumber, 		TBP.SecondaryPayerName, 		TBP.SecondaryPayerMemberID, 		TBP.SecondaryPayerGroupNumber, 		SUM(BD.SpentDuration) TotalDuration, 		TBP.BillingCPTCode, 		TBP.AddOnCPTCode, 		TBP.PrimaryDiagnosis, 		TBP.SecondaryDiagnosis, 		TBP.ReferringProviderName, 		TBP.PracticeName, 		TBP.SecondAddOnCode 	FROM TrnBillingProfile TBP 	INNER JOIN TrnPatientBillingDetail BD ON TBP.PatientID = BD.PatientID AND TBP.MonthOfService = BD.MonthOfService AND BD.IsDeleted = 0 	INNER JOIN Mst_Patient P ON BD.PatientID = p.Patient_ID AND P.Account_ID = @AccountID 	WHERE BD.MonthOfService >= @fromDate and BD.MonthOfService <= @toDate 	Group By 	P.Patient_ID, 		P.Patient_First_Name +' ' + P.Patient_Last_Name , 		P.Patient_DOB, 		TBP.MonthOfService, --AS BillableDate, 		TBP.PrimaryPayerName, 		TBP.PrimaryPayerMemberID, 		TBP.PrimaryPayerGroupNumber, 		TBP.SecondaryPayerName, 		TBP.SecondaryPayerMemberID, 		TBP.SecondaryPayerGroupNumber, 		TBP.BillingCPTCode, 		TBP.AddOnCPTCode, 		TBP.PrimaryDiagnosis, 		TBP.SecondaryDiagnosis, 		TBP.ReferringProviderName, 		TBP.PracticeName, 		TBP.SecondAddOnCode END