 Create procedure USP_Update_MAC_MAD_MAH_Score AS BEGIN 	 	--Updating all existing patient below score from imported file record. 	Update Mst_Patient SET  		MACScore=Round((Convert(float, MP.MAC_PDC_2021)*100),2),  		MADScore=Round((Convert(float, MP.MAD_PDC_2021)*100),2), 		MAHScore=Round((Convert(float, MP.MAH_PDC_2021)*100),2) 	FROM Mst_Patient P INNER JOIN Salutem_Temp.DBO.Member_Import MP  	ON P.Patient_MRN = MP.Member_MBI 	SELECT @@ROWCOUNT RowUpdateCount  	INSERT INTO TrnPatientPDCHistoryLog (PatientId,MACScore,MADScore,MAHScore,CreatedOn) 	SELECT P.Patient_ID,P.MACScore,P.MADScore,P.MAHScore,GETDATE() FROM Mst_Patient P 	INNER JOIN Salutem_Temp.DBO.Member_Import MP  	ON P.Patient_MRN = MP.Member_MBI END  --EXEC USP_Update_MAC_MAD_MAH_Score